const AWS = require( 'aws-sdk' )

var sqs = new AWS.SQS( {
	apiVersion: '2012-11-05'
} )

function sendSqsMessage(body) {
    return sqs.sendMessage({
        MessageBody: JSON.stringify({
            queueUrl: process.env.QueueURL,
            ...body
        }),
        QueueUrl: process.env.QueueURL,
    }).promise()
}

function test() {
    return 'Hello from Layer'
}

module.exports = {
    sendSqsMessage,
    test
}